import React from 'react'
import ReactDOM from 'react-dom'

const Part = (props) => {

  return (
    <div>
      <p>
        {props.part} {props.exercises}
      </p>
    </div>
  )
}

const Header = (props) => {

  return (
    <div>
        <h1>
          {props.course}
        </h1>
    </div>
  )
}

const Content = (props) => {

  return (
    <div>
      <p>
        {props[0].name} {props[0].exercises}
        {props[1].name} {props[1].exercises}
        {props[2].name} {props[2].exercises}
      </p>
    </div>
  )
}

const Total = (props) => {

  return (
    <div>
      <p>
      {props.exercisesTitle} {props.totalExercises}
      </p>
    </div>
  )
}

const App = () => {

  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamentals of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ]

  const exercisesTitle = 'Number of exercises'
  const totalExercises = parts[0].exercises + parts[1].exercises + parts[2].exercises

  return (

    <div>
      <Header course = {course} />
      <Content parts = {parts} />
      <Content name = {parts[0].name} exercises = {parts[0].exercises} />
      <Content name = {parts[1].name} exercises = {parts[1].exercises} />
      <Content name = {parts[2].name} exercises = {parts[2].exercises} />
      <Total exercisesTitle = {exercisesTitle} totalExercises = {totalExercises} />
    </div>

  )
}

ReactDOM.render(<App />, document.getElementById('root'))
