import React from 'react'
import ReactDOM from 'react-dom'

const Part = (props, n) => {

  const part = props + n
  const exercises = props + n

  return (
    <div>
        {props.part1} {props.exercises1}
    </div>
  )
}

const Header = (props) => {

  return (
    <div>
        <h1>{props.course}</h1>
    </div>
  )
}

const Content = (props) => {

  return (
    <div>
       <Part props = {props.part1} props = {props.exercises1}/>
       <Part props = {props.part2} props = {props.exercises2}/>
       <Part props = {props.part3} props = {props.exercises3}/>
    </div>
  )
}

const Total = (props) => {

  return (
    <div>
      <p>
      {props.exercisesTitle} {props.totalExercises}
      </p>
    </div>
  )
}

const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  const exercisesTitle = 'Number of exercises'
  const totalExercises = exercises1 + exercises2 + exercises3

  return (
    <div>
      <Header course = { course } />
      <Content part1 = {part1} exercises1 = {exercises1}
               part2 = {part2} exercises2 = {exercises2}
               part3 = {part3} exercises3 = {exercises3} />
      <Total exercisesTitle = {exercisesTitle}
             totalExercises = {totalExercises} />
    </div>
  )
}

ReactDOM.render(<App />, document.getElementById('root'))
